package javaProg;

public class reverseArray {
	
	//Write a Java Program to reverse an array?

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char s[]= {'a','b','c','d','e','f'};
		
		
		for(int i=s.length-1;i>=0;i--) {
			
		System.out.println(s[i]);
		}

	}

}
